import { LOGIN_FAIL, LOGIN_SUCCESS, REGISTER_FAIL, REGISTER_SUCCESS } from "./types";
import axios from "axios";
import { setAlert } from "./alert";


export const registerCustomer=({name,email,password})=>async(dispatch)=>{

    const config={
        headers:{
            "Content-Type":"Application/json",
        },
    }
    const body=JSON.stringify({name,email,password});
    try{
        const res= await axios.post("http://localhost:4000/api/users/registerCustomer",body,config);
        console.log(res.data);
        dispatch({type:REGISTER_SUCCESS,payload:res.data});
        history.pushState("/login");
    }
    catch(err){
        const errors = err.response.data.errors;
    // console.log(err);
    if (errors) {
      errors.forEach((error) => dispatch(setAlert(error.msg, "danger")));
    }
    dispatch({ type: REGISTER_FAIL });
  }
}
