import React, { Fragment, useState } from "react";
import { Button, Modal } from "react-bootstrap";
import Cart from "../Cart/Cart";

const CheckoutForm = ({ show, setShow, handleClose, handleShow }) => {
  const [formData, setFormData] = useState({
    country: "",
    fullName: "",
    streetAddress: "",
    city: "",
    state: "",
    zipCode: "",
    phoneNumber: "",
  });
  const {
    country,
    fullName,
    streetAddress,
    city,
    state,
    zipCode,
    phoneNumber,
  } = formData;

  const onChange=(e)=>setFormData({...formData,[e.target.name]:e.target.value})
  return (
    <Fragment>
      {/* <Cart> */}
      <Modal
        size="lg"
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Checkout Form</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* <div> */}
          {/* <div class="py-12"> */}
          {/* <div class="max-w-md mx-auto bg-white shadow-lg rounded-lg md:max-w-xl mx-2">  */}
          <div class="md:flex ">
            <div class="w-full p-4 px-5 py-5">
              <div class="flex flex-row">
                <h2 class="text-3xl font-semibold">Hamro</h2>
                <h2 class="text-3xl text-green-400 font-semibold">Pasal</h2>
              </div>
              <div class="flex flex-row pt-2 text-xs pt-6 pb-5">
                {" "}
                <span class="font-bold">Information</span>{" "}
                <small class="text-gray-400 ml-1"></small>{" "}
                <span class="text-gray-400 ml-1">Shopping</span>{" "}
                <small class="text-gray-400 ml-1"></small>{" "}
                <span class="text-gray-400 ml-1">Payment</span>{" "}
              </div>{" "}
              <span>Customer Information</span>
              <input
                type="text"
                name="fullName"
                class="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
                placeholder="Full Name*"
                value={fullName}
              />{" "}
              <input
                type="text"
                name="country"
                class="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
                placeholder="Country*"
                value={country}

              />
              <input
                type="text"
                name="streetAddress"
                class="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
                placeholder="Street Address (optional)"
                value={streetAddress}

              />{" "}
              <div class="grid md:grid-cols-3 md:gap-2">
                <input
                  type="text"
                  name="zipCode"
                  class="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
                  placeholder="Zipcode (optional)"
                    value={zipCode}

                />
                <input
                  type="text"
                  name="city"
                  class="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
                  placeholder="City (optional)"
                  value={city}
                />{" "}
                <input
                  type="text"
                  name="state"
                  class="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
                  placeholder="State(optional)"
                />{" "}
              </div>{" "}
              <input
                type="text"
                name="phoneNumber"
                class="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
                placeholder="Phone Number*"
              />
              <div class="flex justify-between items-center pt-2">
                {" "}
                <button
                  type="button"
                  onClick={handleClose}
                  class="h-12 w-24 text-blue-500 text-xs font-medium"
                >
                  Return to cart
                </button>
                <button
                  type="button"
                  class="h-12 w-48 rounded font-medium text-xs bg-blue-500 text-white"
                >
                  Continue to Shipping
                </button>{" "}
              </div>
            </div>
          </div>
          {/* </div> */}
          {/* </div> */}
          {/* </div> */}
        </Modal.Body>
        {/* <Modal.Footer>
          <Button variant="success" onClick={logout}>
            Logout
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Cancel
          </Button>
        </Modal.Footer> */}
      </Modal>

      {/* </Cart> */}
    </Fragment>
  );
};

export default CheckoutForm;
