import axios from "../axios/axios";
import { setAlert } from "./alert";
import { ADD_CATEGORY, EDIT_CATEGORY, DELETE_CATEGORY } from "./types";

export const AddCategory = (formData) => async (dispatch) => {
  // console.log(token);
  try {
    const token = localStorage.getItem("token");
    // const body = formData;
    // const res = await axios.post('/category/add-category', formData, {
    //   headers: { "x-auth-token": token },
    // });
    const res = await axios.post("/category/add-category", formData, {
      headers: {
        "x-auth-token": token,
        "Content-Type": "Application/json",
      },
    });
    // console.log(res.data);
    dispatch({ type: ADD_CATEGORY, payload: res.data });
    dispatch(setAlert("Category added successfully", "success"));
  } catch (err) {
      dispatch(setAler("Choose unique category", "success"));
    // console.log(err);
  }
};
