import { PRODUCTS,CHEAP_PRODUCTS,EXPENSIVE_PRODUCTS,OLD_PRODUCTS,NEW_PRODUCTS} from "../actions/types";

const initialState={
    products:[],
    loading:true
}

export default function(state=initialState,action){
    const {type,payload}=action;
    switch(type){
        case PRODUCTS:
        case CHEAP_PRODUCTS:
            return {...state,...payload,loading:false}
        default:
            return state;

    }
}
