import React,{useState,useEffect} from "react";
import { Table, Container, Alert, Button } from "react-bootstrap";
import AddCategoryModal from "./AddCategory";
// import {useDispatch, useSelector} from "react-redux";
// import { adminCategories } from "../../actions/adminAction";


const Category = ({categories,loading}) => {
  console.log(categories);
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const renderCategories=categories.map(category=>{
    return (
         <tr key={category._id}>
            <td>{category.name}</td>
            <td><i class="fas fa-2x
            fa-edit" style={{color:'blue'}}></i>
            <i class="fas fa-trash"></i>
            </td>
          </tr>
    )
  })

  return (
    <Container>
      <AddCategoryModal
        show={show}
        setShow={setShow}
        handleClose={handleClose}
        handleShow={handleShow}
      />
      <Alert.Heading style={{ width: "20%" }}>
        <Button variant="danger"  onClick={handleShow} >Add Category</Button>
      </Alert.Heading>
      <Table striped bordered hover variant="warning">
        <thead>
          <tr>
            <th>Category Name</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
         {renderCategories}
        </tbody>
      </Table>
    </Container>
  );
};

export default Category;
