import React, { useEffect } from "react";
import { Fragment } from "react";
import { useDispatch, useSelector } from "react-redux";
import {Link} from "react-router-dom";
import { customerProducts } from "../../actions/products";
import camera from "./camera.jpg";
import headphone from "./headphone.jpeg";
import watch from "./watch.jpg";


const ProductList =()=>{
    //  const classNamees = useStyles();

    const dispatch=useDispatch();

    const {products,loading}=useSelector(state=>state.customerProduct);

    useEffect(()=>{
      dispatch(customerProducts());

    })
    const renderProducts=products.map(product=>{
      // console.log(product.photo);
      const url= "http://localhost:4000"+product.photo
      // console.log(url);
      // const url=product.photo
      // const url=`http://localhost:4000${product.photo}`;
      // const url=product.photo
      // console.log(url);
      return (
        <Fragment key={product._id}>
          <div className="lg:w-1/4 md:w-1/2 p-4 w-full">
          <a className="block relative h-48 rounded overflow-hidden">
          <img alt="ecommerce" src={url} className="object-cover object-center w-full h-full block"/>
          </a>
        <div className="mt-4">
          <h3 className="text-gray-500 text-xs tracking-widest title-font mb-1">{product.categoryID}</h3>
          <h2 className="text-gray-900 title-font text-lg font-medium">{product.title}</h2>
          <p className="mt-1">Nrs.{product.price}</p>
        </div>
      </div>
        </Fragment>
      )
    })

    return (
        <div classNameName="product-container">
            <br />
             <div className="product-header container px-5">


                 <div>
                 <ul classNameName="navbar-nav mb-2 mb-lg-0 ml-auto ml-auto">
         <li classNameName="nav-item dropdown">
          <a classNameName="nav-link active dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            <i classNameName="fab fa-2x fa-accessible-icon" style={{color:'red'}}></i>&nbsp;Filter Products
          </a>
          {/* <br /> */}
          <ul classNameName="dropdown-menu" aria-labelledby="navbarDropdown">
            <li classNameName="active"><Link to="/category" classNameName="dropdown-item" href="#">Action</Link></li>
            <li classNameName="active"><hr classNameName="dropdown-divider" /><Link to="/category" classNameName="dropdown-item">Another action</Link></li>
            <li classNameName="active"><hr classNameName="dropdown-divider" /></li>
            <li classNameName="active"><Link to="/category" classNameName="dropdown-item">Something else here</Link></li>
          </ul>
        </li>
        </ul>
    </div>    
         <form classNameName="d-flex">
        <input classNameName="form-control me-2" type="search" placeholder="Search product" aria-label="Search" />
        {/* <button classNameName="btn btn-outline-success" style={{borderRadius :'0px'}} type="submit">Search</button> */}
      <button type="submit"><i classNameName="fas fa-3x fa-search" type="submit"></i></button>
      </form>
        </div>
    <section className="text-gray-600 body-font">
  <div className="container px-5 py-24 mx-auto">
    <div className="flex flex-wrap -m-4">
      {renderProducts}
    </div>
  </div>
</section>            
</div>

    );
}

export default ProductList;
