import { Modal ,Form} from "react-bootstrap";
import React, { useEffect, useState } from "react";

import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';

import {useDispatch,useSelector} from "react-redux";

const AddProductModal = ({ show, setShow, handleClose, handleShow }) => {

    const dispatch=useDispatch();
    const [formData,setFormData]=useState({
        title:"",
        price:"",
        category:"",
        stockQuantity:"",
        description:"",
        selectedFile:""
    })
    const {title,price,category,stockQuantity,description,selectedFile}=formData;
    const {categories,loading}=useSelector(state=>state.adminCategory)

    const onChange=(e)=>{
        setFormData({...formData,[e.target.name]:e.target.value})
    }
    const onFileChange=(e)=>{
        setFormData({selectedFile:e.target.files[0]})
    }
    const ProductAdd=()=>{
        const {title,price,category,stockQuantity,description,selectedFile}=formData;
        let data=new FormData();
         formData.append('title',title);
    formData.append('price',price);
    formData.append('stockQuantity',stockQuantity);
    formData.append('description',description);
    formData.append('selectedFile',selectedFile);
    }
  return (
    <Modal
      size="md"
      show={show}
      onHide={handleClose}
      backdrop="static"
      keyboard={false}
    >
      <Modal.Header closeButton>
        <Modal.Title>Product</Modal.Title>
      </Modal.Header>
      <Modal.Body>
          <TextField
            id="category"
            select
            fullWidth
            name="category"
            label="Select category"
            value={category}
            onChange={(e) => onChange(e)}
            // onChange={this.onDepartmentChange}
            // helperText="Select Category"
              >

          {categories.map((category) => (
            <MenuItem key={category.value} value={category._id}>
              {category.name}
            </MenuItem>
          ))}
        </TextField>
        <input
          type="text"
          name="title"
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="Enter product name"
            value={title}
            onChange={(e) => onChange(e)}
        />        
        <input
          type="number"
          name="price"
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="Enter price"
            value={price}
            onChange={(e) => onChange(e)}
        />
        <input
          type="number"
          name="stockQuantity"
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="Stock Quantity"
            value={stockQuantity}
            onChange={(e) => onChange(e)}
        />
         <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="selectedFile"
              // value={selectedFile}
              // label="Assignment File"
              type="file"
              id="selectedFile"
              onChange={onFileChange}
              autoComplete="selectedFile"
            />
        <Form.Group controlId="exampleForm.ControlTextarea1">
          <Form.Label>Description</Form.Label>
          <Form.Control as="textarea" rows={3}  name="description" value={description} />
        </Form.Group>
        <div className="flex justify-between items-center pt-2">
          {" "}
          <button
            type="button"
            onClick={handleClose}
            className="h-12 w-24 text-blue-500 text-xs font-medium"
          >
            Cancel
          </button>
          <button
              type="button"
            onClick={ProductAdd}
            // onClick={!id ? addCategory : (()=>EditCategory(id))}
             className="btn btn-danger"
            className="h-12 w-48 rounded font-medium text-xs bg-blue-500 text-white"
          >
            Save
            {/* Continue to Shipping */}
          </button>{" "}
        </div>
      </Modal.Body>
    </Modal>
  );
};

export default AddProductModal;
