import React, { useState } from "react";
import {Modal} from "react-bootstrap";

import {useDispatch} from "react-redux"
import {getCategory} from "../../actions/adminAction";

const EditCategoryModal=({ show, setShow, handleClose, handleShow })=>{
    const [formData,setFormData]=useState({
      name:""
    })
    const onChange=(e)=>setFormData({...formData,[e.target.name]:e.target.value})
    const {name}=formData;
    // const dispatch=useDispatch();
    // handleShow=(id)=>{
    //     console.log(id);
    // }
    const addCategory=async()=>{
    //    dispatch(AddCategory(formData));
       handleClose()
    }
    return(
        <Modal
        size="md"
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Edit Category</Modal.Title>
        </Modal.Header>
        <Modal.Body>
             {/* <div className="grid md:grid-cols-3 md:gap-2"> */}
                <input
                  type="text"
                  name="name"
                  className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
                  placeholder="Enter category name"
                  value={name}
                  onChange={(e) => onChange(e)}
                />
              {/* </div>{" "} */}

              <div className="flex justify-between items-center pt-2">
                {" "}
                <button
                  type="button"
                  onClick={handleClose}
                  className="h-12 w-24 text-blue-500 text-xs font-medium"
                >
                  Cancel
                </button>
                <button
                  type="button"
                    onClick={addCategory}
                  //   className="btn btn-danger"
                  className="h-12 w-48 rounded font-medium text-xs bg-blue-500 text-white"
                >
                    Update Category
                  {/* Continue to Shipping */}
                </button>{" "}
              </div>
        </Modal.Body>
        </Modal>
    )
} 

export default EditCategoryModal;
