import axios from "../axios/axios";
import { setAlert } from "./alert";
import { ADD_CATEGORY, EDIT_CATEGORY, DELETE_CATEGORY } from "./types";

export const AddCategory = (formData) => async (dispatch) => {
  try {
    const token = localStorage.getItem("token");
    console.log(token);
    // const body = {};
    // const headers = {
    //   "Content-Type": "application/json",
    //   "x-auth-token": token,
    // };
    // headers: { "x-auth-token": token, "Content-Type": "Application/json" },

    // const config = {
    //   headers: { "Content-Type": "Application/json","x-auth-token":token},
    // };
    const body = formData;
    const res = await axios.post('/category/add-category', body, {
      headers: { "x-auth-token": token },
    });
    console.log(res.data);
    // console.log(res.data.newCartItems.subTotal);
    dispatch({ type: ADD_CATEGORY, payload: res.data });
    dispatch(setAlert("Category added to successfully", "success"));
    // history.push("/cart");
    // console.log(res.data);
  } catch (err) {
    console.log(err);
  }
};
