import { Modal, Form } from "react-bootstrap";
import React, { useEffect, useState } from "react";

import TextField from "@material-ui/core/TextField";
import MenuItem from "@material-ui/core/MenuItem";

import { useDispatch, useSelector } from "react-redux";
import { AddProduct } from "../../actions/adminAction";

const AddProductModal = ({ show, setShow, handleClose, handleShow }) => {
  const dispatch = useDispatch();
  const [formData, setFormData] = useState({
    title: "",
    price: 0,
    category: "",
    stockQuantity: 0,
    description: "",
    selectedFile: '',
  });
  const { title, price, category, stockQuantity, description, selectedFile } =
    formData;

  //   const onChange = (e) => {
  //     setFormData({ ...formData, [e.target.name]: e.target.value });
  //   };
  const onCategoryChange = (e) => {
    setFormData({ category: e.target.value })
    //   console.log(e.target.value)
  }
  const onName = (e) => {
    setFormData({ title: e.target.value })
  }
  const onPrice = (e) => {
    setFormData({ price: e.target.value })
  }
  const onStockQuantity = (e) => {
    setFormData({ stockQuantity: e.target.value })
  }
  //   const onFileChange=(e)=>{
  //     setFormData({...selectedFile:e.target.files[0]});
  //     console.log(selectedFile);
  //   }
  const onDescription = (e) => {
    setFormData({ description: e.target.value })
  }



  const ProductAdd = () => {
    // console.log(category);
    let data = new FormData();
    data.append("title", title);
    data.append("price", price);
    data.append("stockQuantity", stockQuantity);
    data.append("description", description);
    data.append("selectedFile", selectedFile);
    console.log(selectedFile);
    dispatch(AddProduct(data, category));

    handleClose()

  };
  const { categories, loading } = useSelector((state) => state.adminCategory);
  // console.log(selectedFile);
  return (
    <Modal
      size="md"
      show={show}
      onHide={handleClose}
      backdrop="static"
      keyboard={false}
    >
      <Modal.Header closeButton>
        <Modal.Title>Product</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* <TextField
          id="category"
          select
          fullWidth
          name="category"
          label="Select category"
          value={category}
          onChange={(e) => onChange(e)}
          // onChange={this.onDepartmentChange}s
          // helperText="Select Category"
        >
          {categories.map((category) => (
            <MenuItem key={category._id} value={category._id}>
              {category.name}
            </MenuItem>
          ))}
        </TextField> */}

        <input
          type="text"
          name="title"
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="Enter product name"
          value={title}
          onChange={onName}
        />
        <input
          type="number"
          name="price"
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="Enter price"
          value={price}
          onChange={onPrice}
        />
        <input
          type="number"
          name="stockQuantity"
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="Stock Quantity"
          value={stockQuantity}
          onChange={onStockQuantity}
        />

        <Form.Group controlId="exampleForm.ControlTextarea1">
          <Form.Label>Description</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            name="description"
            value={description}
            onChange={onDescription}
          />
        </Form.Group>
          <input
          type="file"
          name="selectedFile"
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="CHoose Picture"
          value={selectedFile}
          onChange={(e)=>{setFormData({...formData,selectedFile:e.target.files[0]})}}
        />
        {/* <TextField
          variant="outlined"
          margin="normal"
          required
          fullWidth
          name="selectedFile"
          // value={selectedFile}
          // label="Assignment File"
          type="file"
          id="selectedFile"
          onChange={(e) => setFormData({ ...formData, selectedFile: e.target.files[0] })}
          //   onChange={onFileChange}
          autoComplete="selectedFile"
        /> */}
        <TextField
          id="standard-select-department"
          select
          fullWidth
          name="category"
          label="Select"
          value={category}
          onChange={onCategoryChange}
          helperText="Please select category"
        >
          {categories.map((category) => (
            <MenuItem key={category._id} value={category._id}>
              {category.name}
            </MenuItem>
          ))}
        </TextField>
        <div className="flex justify-between items-center pt-2">
          {" "}
          <button
            type="button"
            onClick={handleClose}
            className="h-12 w-24 text-blue-500 text-xs font-medium"
          >
            Cancel
          </button>
          <button
            type="button"
            onClick={ProductAdd}
            // onClick={!id ? addCategory : (()=>EditCategory(id))}
            className="btn btn-danger"
            className="h-12 w-48 rounded font-medium text-xs bg-blue-500 text-white"
          >
            Save
            {/* Continue to Shipping */}
          </button>{" "}
        </div>
      </Modal.Body>
    </Modal>
  );
};

export default AddProductModal;
