import { Modal ,Form} from "react-bootstrap";
import React, { useEffect, useState } from "react";

import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';


const AddProductModal = ({ show, setShow, handleClose, handleShow }) => {

    const [formData,setFormData]=useState({
        title:"",
        price:"",
        stockQuantity:"",
        description:"",

    })
  return (
    <Modal
      size="md"
      show={show}
      onHide={handleClose}
      backdrop="static"
      keyboard={false}
    >
      <Modal.Header closeButton>
        <Modal.Title>Product</Modal.Title>
      </Modal.Header>
      <Modal.Body>
          <TextField
            id="category"
            select
            fullWidth
            name="category"
            label="Select category"
            value=""
            // onChange={this.onDepartmentChange}
            // helperText="Select Category"
              >
        <MenuItem>
            Category1
        </MenuItem>
          {/* {departments.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))} */}
        </TextField>
        <input
          type="text"
          name="title"
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="Enter product name"
          //   value={name}
          //   onChange={(e) => onChange(e)}
        />        
        <input
          type="number"
          name=""
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="Enter price"
          //   value={name}
          //   onChange={(e) => onChange(e)}
        />
        <input
          type="number"
          name=""
          className="border rounded h-10 w-full focus:outline-none focus:border-green-200 px-2 mt-2 text-sm"
          placeholder="Stock Quantity"
          //   value={name}
          //   onChange={(e) => onChange(e)}
        />
         <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="file"
              // value={selectedFile}
              // label="Assignment File"
              type="file"
              id="file"
            //   onChange={this.onFileChange}
              autoComplete="file"
            />
        <Form.Group controlId="exampleForm.ControlTextarea1">
          <Form.Label>Description</Form.Label>
          <Form.Control as="textarea" rows={3} />
        </Form.Group>
        <div className="flex justify-between items-center pt-2">
          {" "}
          <button
            type="button"
            onClick={handleClose}
            className="h-12 w-24 text-blue-500 text-xs font-medium"
          >
            Cancel
          </button>
          <button
            //   type="button"
            // onClick={!id ? addCategory : (()=>EditCategory(id))}
            //   className="btn btn-danger"
            className="h-12 w-48 rounded font-medium text-xs bg-blue-500 text-white"
          >
            Save
            {/* Continue to Shipping */}
          </button>{" "}
        </div>
      </Modal.Body>
    </Modal>
  );
};

export default AddProductModal;
