import React,{useState,useEffect} from "react";
import { Table, Container, Alert, Button } from "react-bootstrap";
import AddCategoryModal from "./AddCategory";
import {useDispatch, useSelector} from "react-redux";
import { adminCategories } from "../../actions/adminAction";

const Category = () => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const dispatch=useDispatch();
  useEffect(()=>{
    dispatch(adminCategories())
  },[dispatch])

  const {categories,loading}=useSelector(state=>state.adminCategory.categories)
  console.log(categories);
  return (
    <Container>
      <AddCategoryModal
        show={show}
        setShow={setShow}
        handleClose={handleClose}
        handleShow={handleShow}
      />
      <Alert.Heading style={{ width: "20%" }}>
        <Button variant="danger"  onClick={handleShow} >Add Category</Button>
      </Alert.Heading>
      <Table striped bordered hover variant="warning">
        <thead>
          <tr>
            <th>Category Name</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td>Mark</td>
          </tr>
        </tbody>
      </Table>
    </Container>
  );
};

export default Category;
