import React, {useState, useEffect } from "react";
import { Link,withRouter, useHistory } from "react-router-dom";
import { connect, useSelector} from "react-redux";
// import alert from "../../reducers/alert";
import { setAlert } from "../../actions/alert";
import { registerCustomer } from "../../actions/auth";

const SignUp = ({ setAlert, registerCustomer, history }) => {
  // const token=useSelector(state=>state.auth.token);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    password2: "",
  });
  const { name, email, password, password2 } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    if (password !== password2) {
      //   console.log("password  not match");
      setAlert("Password of two field do not match", "red");
    } else {
      console.log("Customer is going to be register");
      registerCustomer({ name, email, password }, history);
      // history.push("/login");
    }
  };
  const token = useSelector((state) => state.auth.token);
  // const browserHistory = useHistory();
  useEffect(() => {
    if (token) {
      history.push("/products");
    }
  }, [token,history]);

  //   const token=useSelector(state=>state.auth.token);
  //   const browserHistory=useHistory()
  // useEffect(()=>{
  //   if(token){
  //     browserHistory.push('/products')
  //   }
  // },[])

  return (
    //  <Fragment>
    <div className="product-container">
      <div class="min-h-screen flex items-center justify-center bg-gray-50 py-8 px-4 sm:px-6 lg:px-8 signup-container">
        <div class="max-w-md w-full space-y-8">
          <div>
            <img
              class="mx-auto h-12 w-auto"
              src="https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg"
              alt="Workflow"
            />
            <h2 class="mt-6 text-center text-3xl font-extrabold text-gray-900">
              Sign Up your account
            </h2>
            <p class="mt-2 text-center text-sm text-gray-600">
              Already Have an account? &nbsp;
              <Link
                to="/login"
                class="font-medium text-indigo-600 hover:text-indigo-500"
              >
                Sign In
              </Link>
            </p>
          </div>
          <form onSubmit={(e) => onSubmit(e)}>
            {/* <input type="hidden" name="remember" value="true" /> */}
            <div class="rounded-md shadow-sm -space-y-px">
              <div>
                <label for="name" class="sr-only">
                  Name
                </label>
                <input
                  id="name"
                  onChange={(e) => onChange(e)}
                  name="name"
                  value={name}
                  type="text"
                  autocomplete="email"
                  class="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Name"
                />
              </div>
              <br />
              <div>
                <label for="email-address" class="sr-only">
                  Email address
                </label>
                <input
                  id="email-address"
                  onChange={(e) => onChange(e)}
                  name="email"
                  value={email}
                  type="email"
                  autocomplete="email"
                  class="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Email address"
                />
              </div>
              <br />
              <div>
                <label for="password" class="sr-only">
                  Password
                </label>
                <input
                  id="password"
                  onChange={(e) => onChange(e)}
                  name="password"
                  value={password}
                  type="password"
                  autocomplete="current-password"
                  class="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Password"
                />
              </div>
              <br />
              <div>
                <label for="password" class="sr-only">
                  Confirm password
                </label>
                <input
                  id="password"
                  onChange={(e) => onChange(e)}
                  name="password2"
                  value={password2}
                  type="password"
                  autocomplete="current-password"
                  class="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder=" Confirm Password"
                />
              </div>
            </div>
            <br />
            <div class="flex items-center justify-between">
              {/* <div class="flex items-center"> */}
              {/* <input id="remember_me" name="remember_me" type="checkbox" class="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded" />
          <label for="remember_me" class="ml-2 block text-sm text-gray-900">
            Remember me
          </label> */}
              {/* </div> */}

              {/* <div class="text-sm">
          <a href="#" class="font-medium text-indigo-600 hover:text-indigo-500">
            Forgot your password?
          </a>
        </div> */}
            </div>

            <div>
              <button
                type="submit"
                value="Register"
                class="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                {/* <input type="submit" value="Register" class="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500" /> */}
                <span class="absolute left-0 inset-y-0 flex items-center pl-3">
                  <svg
                    class="h-5 w-5 text-indigo-500 group-hover:text-indigo-400"
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 20 20"
                    fill="currentColor"
                    aria-hidden="true"
                  >
                    <path
                      fill-rule="evenodd"
                      d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z"
                      clip-rule="evenodd"
                    />
                  </svg>
                </span>
                Sign Up
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>

    // </Fragment>
  );
};

// const mapStateToProps = (state) => ({
//   isAuthenticated: state.auth.isAuthenticated,
// });

export default connect(null, { setAlert, registerCustomer })(
  withRouter(SignUp)
);
// export default connect(null, { createProfile })(withRouter(CreateProfile));
