import React,{Fragment} from "react"
import {Link} from "react-router-dom";

import {connect, useSelector} from "react-redux";
import { logoutCustomer } from "../../actions/auth";

// import Carou from "./Landing";
// import estore from "./estore.jpg"

const Navbar=({logout})=>{
  const GuestLinks=(
    <Fragment>
    <li className="nav-item">
                <Link to="/login" className="nav-link"><i className="fas fa-2x fa-user"></i>&nbsp;Sign In</Link>
            </li>
            <li className="nav-item">
                <Link to="/signup" className="nav-link"><i className="fas fa-2x fa-user-plus"></i>&nbsp;Sign Up</Link>
            </li>
    </Fragment>
  )

  const AuthLinks=(
     <li className="nav-item">
                <a onClick={logout} className="nav-link"><i className="fas fa-2x fa-sign-out-alt"></i>&nbsp;Sign Out</a>
    </li>
  )
  // access token from redux store
    const token=useSelector(state=>state.auth.token);
    return(
<Fragment>
<nav className="navbar navbar-expand-lg navbar-dark bg-dark">
  <div className="container-fluid">
      <Link to="/" className="navbar-brand" style={{fontStyle: "italic"}}>
      {/* <img src="" alt="" width="30" height="24" className="d-inline-block align-text-top" /> */}
      <i className="fab fa-2x fa-shopify" style={{color:"green"}}></i>&nbsp;Hamro Pasal
    </Link>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent" >
       <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
                <a className="nav-link" href="#"><i className="fas fa-2x fa-home" style={{color: 'red'}}></i>&nbsp;Home<span className="sr-only">(current)</span></a>
            </li>
            <Fragment>
            {token ? AuthLinks: GuestLinks}
            </Fragment>

        </ul>

      <ul className="navbar-nav mb-2 mb-lg-0 ml-auto ml-auto">
         <li className="nav-item dropdown">
          <a className="nav-link active dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            <i className="fab fa-2x fa-accessible-icon" style={{color:'blue'}}></i>&nbsp;Categories
          </a>
          {/* <br /> */}
          <ul className="dropdown-menu category-list" aria-labelledby="navbarDropdown">
            <li className="list-style active"><Link to="/category" className="dropdown-item" href="#">Action</Link></li>
            <li className="list-style active"><hr className="dropdown-divider" /><Link to="/category" className="dropdown-item">Another action</Link></li>
            <li className="list-style"><hr className="dropdown-divider" /></li>
            <li className="list-style"><Link to="/category" className="dropdown-item">Something else here</Link></li>
          </ul>
        </li>
        <li className="nav-item">
          <Link to="/products" className="nav-link active" aria-current="page"><i className="fab fa-2x fa-product-hunt"></i>&nbsp;Products</Link>
        </li>
        <li className="nav-item">
          <Link to="/cart" className="nav-link active" ><i className="fas fa-2x fa-shopping-cart" style={{color:'purple'}}></i>&nbsp;Cart</Link>
        </li>

        <br />

      </ul>

    </div>
  </div>
</nav>

</Fragment>

    )
}

const mapStateToProps = (state) => ({
  auth: state.auth,
});
export default connect(mapStateToProps,{logoutCustomer})(Navbar);
