import { ADD_TO_CART,REMOVE_FROM_CART } from "../actions/types";

const initialState={
    products:[],
    subTotal:"",
    loading:true
}


export default function(state=initialState,action){
    const {type,payload}=action;
    switch(type){
        case ADD_TO_CART:
            return {...state,products:payload.products,subTotal:payload.subTotal,loading:false}

        case REMOVE_FROM_CART:
            return state;

        default:
            return state;
    }
}
