import React, { useEffect,useState } from "react";
import { Fragment } from "react";
import { useDispatch, useSelector } from "react-redux";
import {Link} from "react-router-dom";
import { customerProducts } from "../../actions/products";
import camera from "./camera.jpg";
import headphone from "./headphone.jpeg";
import watch from "./watch.jpg";

// react pagination

import ReactPaginate from "react-paginate";

const ProductList =()=>{
    //  const classNamees = useStyles();

    const [pageNumber,setPageNumber]=useState(0);

    const dispatch=useDispatch();

    const {products,loading}=useSelector(state=>state.customerProduct);

    useEffect(()=>{
      dispatch(customerProducts());

    })
    const renderProducts=products.map(product=>{
      var str=product.photo
      var url=str.replace("/home/sushil/2021/E-Commerce/api/uploads/","")
      // console.log(url);
      var source="http://localhost:4000/uploads/"+url;
      return (
        <Fragment key={product._id}>
          <div className="lg:w-1/4 md:w-1/2 p-4 w-full">
          <a className="block relative h-48 rounded overflow-hidden">
          <img alt="ecommerce" src={source} className="object-cover object-center w-full h-full block"/>
          </a>
        <div className="mt-4">
          <h3 className="text-gray-500 text-xs tracking-widest title-font mb-1">{product.categoryID}</h3>
          <h2 className="text-gray-900 title-font text-lg font-medium">{product.title}</h2>
          <p className="mt-1">Nrs.{product.price}</p>
        </div>
      </div>
        </Fragment>
      )
    })

    return (
         <div className="product-container">
            <br />
             <div class="product-header container px-5">


                 <div>
                 <ul className="navbar-nav mb-2 mb-lg-0 ml-auto ml-auto">
         <li className="nav-item dropdown">
          <a className="nav-link active dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            <i className="fab fa-2x fa-accessible-icon" style={{color:'red'}}></i>&nbsp;Filter Products
          </a>
          {/* <br /> */}
          <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
            <li className="active"><Link to="/category" className="dropdown-item" href="#">Action</Link></li>
            <li className="active"><hr className="dropdown-divider" /><Link to="/category" className="dropdown-item">Another action</Link></li>
            <li className="active"><hr className="dropdown-divider" /></li>
            <li className="active"><Link to="/category" className="dropdown-item">Something else here</Link></li>
          </ul>
        </li>
        </ul>
    </div>    
         <form className="d-flex">
        <input className="form-control me-2" type="search" placeholder="Search product" aria-label="Search" />
        {/* <button className="btn btn-outline-success" style={{borderRadius :'0px'}} type="submit">Search</button> */}
      <button type="submit"><i className="fas fa-3x fa-search" type="submit"></i></button>
      </form>
        </div>
    <section class="text-gray-600 body-font">
  <div class="container px-5 py-24 mx-auto">
    <div class="flex flex-wrap -m-4">
      {renderProducts}
    </div>
  </div>
</section>            
</div>

    );
}

export default ProductList;
